// Іммутабельна змінна
let x = 5;
// x = 10;  // Помилка: неможливо змінити

// Мутабельна змінна
let mut y = 5;
y = 10;  // OK

// Константа: компіляції
const MAX: i32 = 1000;

// Без параметрів
fn greet() {
    println!("Hello!");
}

// З параметрами
fn add(a: i32, b: i32) -> i32 {
    a + b  // Без return, останній вираз = результат
}

// Використання
fn main() {
    greet();
    let result = add(5, 3);
    println!("Result: {}", result);
}
