let x = 5;
if x > 3 {
    println!("x більше 3");
} else if x == 3 {
    println!("x дорівнює 3");
} else {
    println!("x менше 3");
}

// loop: нескінченний
let mut i = 0;
loop {
    println!("i = {}", i);
    i += 1;
    if i >= 3 { break; }
}

// while
let mut j = 0;
while j < 5 {
    println!("j = {}", j);
    j += 1;
}

// for
for k in 0..5 {
    println!("k = {}", k);
}
